import UIKit

protocol InDetailScreenDisplayLogic: AnyObject {
    func displaySomething(viewModel: InDetailScreen.Something.ViewModel)
}

class InDetailScreenViewController: UIViewController, InDetailScreenDisplayLogic {
    
    var interactor: InDetailScreenBusinessLogic?
    var router: (NSObjectProtocol & InDetailScreenRoutingLogic & InDetailScreenDataPassing)?
    // MARK: Object lifecycle
    
    init() {
        super.init(nibName: nil, bundle: nil)
        self.setup()
    }
    
    required init?(coder: NSCoder) {
        fatalError("init(coder:) has not been implemented")
    }
    
    // MARK: Setup
    
    private func setup() {
        let viewController = self
        let interactor = InDetailScreenInteractor()
        let presenter = InDetailScreenPresenter()
        let router = InDetailScreenRouter()
        viewController.interactor = interactor
        viewController.router = router
        interactor.presenter = presenter
        presenter.viewController = viewController
        router.viewController = viewController
        router.dataStore = interactor
    }
    
    // MARK: Routing
    
    override func prepare(for segue: UIStoryboardSegue, sender: Any?) {
        if let scene = segue.identifier {
            let selector = NSSelectorFromString("routeTo\(scene)WithSegue:")
            if let router = router, router.responds(to: selector) {
                router.perform(selector, with: segue)
            }
        }
    }
    
    // MARK: View lifecycle
    
    override func viewDidLoad() {
        super.viewDidLoad()
        doSomething()
        
        view.backgroundColor = .yellow
        setupUI()
    }
    
    
    private lazy var button: UIButton = {
        let button = UIButton()
        button.backgroundColor = .systemGray
        button.translatesAutoresizingMaskIntoConstraints = false
        button.addTarget(self, action: #selector(goToSecondScreen), for: .touchUpInside)
        return button
    }()
    
    @objc
    private func goToSecondScreen() {
    }
    
    private func setupUI() {
        view.addSubview(button)
        
        NSLayoutConstraint.activate([
            button.centerXAnchor.constraint(equalTo: view.centerXAnchor),
            button.centerYAnchor.constraint(equalTo: view.centerYAnchor),
            button.heightAnchor.constraint(equalToConstant: 150),
            button.widthAnchor.constraint(equalToConstant: 150)
        ])
    }
    
    // MARK: Do something
    
    //@IBOutlet weak var nameTextField: UITextField!
    
    func doSomething() {
        let request = InDetailScreen.Something.Request()
        interactor?.doSomething(request: request)
    }
    
    func displaySomething(viewModel: InDetailScreen.Something.ViewModel) {
        //nameTextField.text = viewModel.name
    }
}
